function InfoTooltip({isOpen, onClose, title}) {

	return (

		<div className={`popup ${isOpen ? "popup_opened" : " "}`}>
         <div className="popup__container">
            <button type="button" className="popup__close-btn" onClick={onClose} ></button>
            <h2 className="popup__head">{title}</h2>
				<div className="infotool-tip">
        <img className="infotool-tip__img" alt="icon" src={props.img} />
        <p className="infotool-tip__text">{props.text}</p>
      </div>

         </div>
      </div>
	)
	}

	export default InfoTooltip;
