import { Link, useLocation } from 'react-router-dom';
import logo from '../images/logo.svg';

function Header({loggedIn, email}) {

   const location = useLocation();
   const path = location.pathname === "/sign-in" ? "/sign-up" : "/sign-in";
   const title = location.pathname === "/sign-in" ? "Регистрация" : "Вход";

   return (
      <header className="header">
         <img className="header__logo" src={logo} alt="Логотип" />
         {loggedIn ? (
         <>
         <p className="header__usermail">{email}</p>
         <button className='header__button-signup'>Выход</button>
         </>
         ):

         <Link to="/sign-up" className="register__login-link">Регистрация</Link>
         }

      </header>
   )
}


export default Header;
